package com.kaolafm.payment.utils;

import net.sf.json.JSONObject;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import sun.misc.BASE64Decoder;

import javax.net.ssl.HttpsURLConnection;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.PrintStream;
import java.net.URL;

/**
 * @author gongzf
 * @date 2016/3/24
 */
public class IosPayUtils {

    private Logger logger  = LoggerFactory.getLogger(IosPayUtils.class);

    // 沙箱验证地址
    public static final String url_sandbox="https://sandbox.itunes.apple.com/verifyReceipt";
    //真实验证地址
    public static final String url_verify="https://buy.itunes.apple.com/verifyReceipt";
    /**
     * 解密BASE64字窜
     * @param s
     * @return
     */
    public static String getFromBASE64(String s) {
        byte[] b = null;
        if (s != null) {
            BASE64Decoder decoder = new BASE64Decoder();
            try {
                b = decoder.decodeBuffer(s);
                return new String(b);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
        return new String(b);
    }


    /**
     * 用BASE64加密
     * @param str
     * @return
     */
    public static String getBASE64(String str) {
        byte[] b = str.getBytes();
        String s = null;
        if (b != null) {
            s = new sun.misc.BASE64Encoder().encode(b);
        }
        return s;
    }

    /**
     * 根据原始收据返回苹果的验证地址:
     *  * 沙箱    https://sandbox.itunes.apple.com/verifyReceipt
     * 真正的地址   https://buy.itunes.apple.com/verifyReceipt
     * @param receipt
     * @return Sandbox 测试单   Real 正式单
     */
    public static String getEnvironment(String receipt)
    {
        try{
            JSONObject job = JSONObject.fromObject(receipt);
            if(job.containsKey("environment")){
                String evvironment=job.getString("environment");
                return evvironment;
            }
        }catch(Exception ex){
            ex.printStackTrace();
        }
        return "Real";
    }

    /**
     * 根据参数获取真实请求Url
     * @param verifyState
     * @return
     */
    public static  String getRealUrl(String verifyState){
        String url=url_verify;
        if(verifyState!=null&&verifyState.equals("Sandbox")){
            url=url_sandbox;
        }
        return url;
    }


    public static JSONObject verifyReceipt(String url, String receipt) {
        try {
            HttpsURLConnection connection = (HttpsURLConnection) new URL(url).openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.setAllowUserInteraction(false);
            PrintStream ps = new PrintStream(connection.getOutputStream());
            ps.print("{\"receipt-data\": \"" + receipt + "\"}");
            ps.close();
            BufferedReader br = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            String str;
            StringBuffer sb = new StringBuffer();
            while ((str = br.readLine()) != null) {
                sb.append(str);
            }
            br.close();
            String resultStr = sb.toString();
            JSONObject result = JSONObject.fromObject(resultStr);
            return result;
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }


    public static  void main(String [] args){
        String str = "";
        JSONObject json = verifyReceipt(url_sandbox,str);
        String status = json.getString("status");
        String receipt = json.getString("receipt");
        System.out.println(status);

    }
}
